import { useEffect, useState } from "react";
import Header from "./Header";
import Categories from "./Categories";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";


function Home(){

    const navigate = useNavigate()

    const [products, setproducts] = useState([]);
    const [cproducts, setcproducts] = useState([]);
    const [search, setsearch] = useState('');
    const [cat, setcat] = useState('');

    // useEffect(()=>{
    //     if(!localStorage.getItem('token')){
    //         navigate('/login');
    //     }
    // },[])

    useEffect(()=>{
        const url='http://localhost:4000/get-products';
        axios.get(url)
        .then((res)=>{
            console.log(res);
            if(res.data.products){
                setproducts(res.data.products);
            }
        })
        .catch((err)=>{
            console.log(err);
            alert('Server ERROR');
        })
    },[])

    const handlesearch=(value)=>{
        setsearch(value);
    }

    const handleClick =()=>{
        console.log('products', products);
        let filteredProducts= products.filter((item)=>{
            if(item.pname.toLowerCase().includes(search.toLowerCase())|| item.pdesc.toLowerCase().includes(search.toLowerCase()) || item.category.toLowerCase().includes(search.toLowerCase())){
                return item;
            }
        });
        setproducts(filteredProducts)
    }

    const handleCategory =(value)=>{
        setcat(value);
        let filteredProducts= products.filter((item,index)=>{
            console.log(value,item, "v");
            if(item.category == cat){
                return item;
            }
        })
        setcproducts(filteredProducts)
    }

    return (
        <div>
            <Header search={search} handlesearch={handlesearch} handleClick={handleClick}/>
            <Categories handleCategory={handleCategory}/>
            {!!localStorage.getItem('token') && <Link to ="/add-product"> ADD PRODUCT</Link>}
            

            <div className="d-flex justify-content-center flex-wrap">
                { products && products.length > 0 && 
                    products.map((item, index) => {
                        return (
                            <div key={item._id} className="card m-3">
                                <img width="400px" height="200px" src={'http://localhost:4000/'+ item.pimage}/>
                                <p className="m-2">{item.pname} | {item.category}</p>
                                <h3 className="m-2 text-danger">{item.price}</h3>
                                <p className="m-2 text-success">{item.pdesc}</p>
                                
                            </div>
                        )
                    })}
            </div>

            <div className="d-flex justify-content-center flex-wrap">
                { cproducts && products.length > 0 && 
                    products.map((item, index) => {
                        return (
                            <div key={item._id} className="card m-3">
                                <img width="400px" height="200px" src={'http://localhost:4000/'+ item.pimage}/>
                                <p className="m-2">{item.pname} | {item.category}</p>
                                <h3 className="m-2 text-danger">{item.price}</h3>
                                <p className="m-2 text-success">{item.pdesc}</p>
                                
                            </div>
                        )
                    })}
            </div>
        </div>
    )
}
export default Home;
